import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import { Modal, Button, Form } from 'react-bootstrap';
import { fetchUserPortfolios, addItemToPortfolio, fetchPostById } from './services/apiService';
import './ProductDetail.css';

const ProductDetail = () => {
  const navigate = useNavigate();
  const { productId } = useParams();
  const [showModal, setShowModal] = useState(false);
  const [portfolios, setPortfolios] = useState([]);
  const [selectedPortfolio, setSelectedPortfolio] = useState('');
  const [error, setError] = useState('');
  const [product, setProduct] = useState(null);

  useEffect(() => {
    const getPortfolios = async () => {
      try {
        const response = await fetchUserPortfolios();
        setPortfolios(response.data);
      } catch (error) {
        console.error('Error fetching portfolios:', error);
      }
    };

    getPortfolios();
  }, []);

  useEffect(() => {
    const getProduct = async () => {
      try {
        const response = await fetchPostById(productId);
        setProduct(response.data);
        if (response.data.wishlistId) {
          setSelectedPortfolio(response.data.wishlistId);
        }
      } catch (error) {
        console.error('Error fetching product details:', error);
      }
    };

    getProduct();
  }, [productId]);

  const handleBackClick = () => {
    // Navigate to the Portfolioslist with the wishlistId
    if (selectedPortfolio) {
      navigate(`/portfolioslist/${selectedPortfolio}`);
    } else {
      navigate('/portfolios');
    }
  };

  const handleViewInARClick = () => {
    window.location.href = 'https://mywebar.com/p/Project_0_qbtprz8dtu';
  };

  const handleSaveToPortfolioClick = () => {
    setShowModal(true);
  };

  const handleModalClose = () => {
    setShowModal(false);
  };

  const handleFormSubmit = async (e) => {
    e.preventDefault();

    if (!selectedPortfolio) {
        setError('Please select a portfolio.');
        return;
    }

    try {
        // Check if the item is already in the selected portfolio
        const portfolio = portfolios.find(p => p.id === selectedPortfolio);
        if (portfolio && portfolio.items.includes(productId)) {
            setError('Item already exists in the selected portfolio.');
            return;
        }

        await addItemToPortfolio(selectedPortfolio, productId);
        setShowModal(false);
    } catch (error) {
        console.error('Error saving to portfolio:', error);
        setError('Something went wrong, please try again.');
    }
};


  if (!product) {
    return <p>Loading...</p>;
  }

  return (
    <div className='main_menu_wrapper'>
      <div className="container">
        <div className="mb-4">
          <div className='backbtn'>
            <div>
              <button onClick={handleBackClick} style={{ border: 'none' }}>
                <i className="fa fa-arrow-left"></i>
              </button>
            </div>
            <div>
              <h5 className="black-text">{product.title}</h5>
            </div>
          </div>
        </div>

        <div className='d-flex justify-content-between'>
        <div className='backbtn'>
          <button onClick={handleBackClick}>
          <i className="fa fa-arrow-left"></i>
          </button>
        </div>
        <div>
        <h5 className="black-text">{product.title}</h5>
        </div>
      </div>

        <div className='product-container'>
          <div className='image-container'>
            <h5 className="black-text">{product.title}</h5>
            <div className='col-12'>
              <img src={`http://127.0.0.1:8000/storage/${product.image}`} alt={product.title} className="img-fluid product_img" />
              <p className="product_code">Product Code: <b>{product.slug}</b></p>
            </div>
          </div>

          <div className="row justify-content-center mt-4">
            <div className="col-10 col-md-6">
              <div dangerouslySetInnerHTML={{ __html: product.body }} />
            </div>
          </div>

          <div className='button-container'>
            <button className="portfolio_btn" onClick={handleSaveToPortfolioClick}>
              <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 576 512" style={{ marginRight: '10px' }}>
                <path d="M384 480l48 0c11.4 0 21.9-6 27.6-15.9l112-192c5.8-9.9 5.8-22.1 .1-32.1S555.5 224 544 224l-400 0c-11.4 0-21.9 6-27.6 15.9L48 357.1 48 96c0-8.8 7.2-16 16-16l117.5 0c4.2 0 8.3 1.7 11.3 4.7l26.5 26.5c21 21 49.5 32.8 79.2 32.8L416 144c8.8 0 16 7.2 16 16l0 32 48 0 0-32c0-35.3-28.7-64-64-64L298.5 96c-17 0-33.3-6.7-45.3-18.7L226.7 50.7c-12-12-28.3-18.7-45.3-18.7L64 32C28.7 32 0 60.7 0 96L0 416c0 35.3 28.7 64 64 64l23.7 0L384 480z" />
              </svg> Save to Portfolio
            </button>

            <button className="portfolio_btn" onClick={handleViewInARClick}>
            <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 120 120" style={{ marginRight: '10px' }}>
              <path d="M85.81 120H34.19a8.39 8.39 0 0 1-8.38-8.39V8.39A8.39 8.39 0 0 1 34.19 0h51.62a8.39 8.39 0 0 1 8.38 8.39v103.22a8.39 8.39 0 0 1-8.38 8.39zM34.19 3.87a4.52 4.52 0 0 0-4.51 4.52v103.22a4.52 4.52 0 0 0 4.51 4.52h51.62a4.52 4.52 0 0 0 4.51-4.52V8.39a4.52 4.52 0 0 0-4.51-4.52z"/>
              <path d="M73.7 10.32H46.3L39.28 3.3 42.01.57l5.89 5.88h24.2L77.99.57l2.73 2.73-7.02 7.02zM47.1 103.23h25.81v3.87H47.1z"/>
            </svg>  View in AR
            </button>
          </div>
        </div>

        <Modal show={showModal} onHide={handleModalClose}>
          <Modal.Header closeButton>
            <Modal.Title>Select Portfolio</Modal.Title>
          </Modal.Header>
          <Modal.Body>
            {error && <p className="text-danger">{error}</p>}
            <Form onSubmit={handleFormSubmit}>
              <Form.Group controlId="formPortfolio">
                <Form.Label>Select Wishlist (Portfolio)</Form.Label>
                <Form.Control
                  as="select"
                  value={selectedPortfolio}
                  onChange={(e) => setSelectedPortfolio(e.target.value)}
                  required
                >
                  <option value="">Select a portfolio</option>
                  {portfolios.map((portfolio) => (
                    <option key={portfolio.id} value={portfolio.id}>
                      {portfolio.wishlist}
                    </option>
                  ))}
                </Form.Control>
              </Form.Group>

              <Button variant="primary" type="submit" className="mt-4">
                Save to Portfolio
              </Button>
            </Form>
          </Modal.Body>
        </Modal>
      </div>
    </div>
  );
};

export default ProductDetail;
