import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const Portfolioslist = () => {
  const navigate = useNavigate();
  const [posts, setPosts] = useState([]);

  useEffect(() => {
    const fetchPosts = async () => {
      try {
        const response = await axios.get('http://127.0.0.1:8000/api/posts');
        setPosts(response.data);
      } catch (error) {
        console.error('Error fetching posts:', error);
      }
    };

    fetchPosts();
  }, []);

  const handleCardClick = (postId) => {
    navigate(`/productdetail/${postId}`);
  };

  return (
    <div className="main_menu_wrapper container-fluid">
      <div className='d-flex justify-content-between'>
        <div className='backbtn'>
          <button className="pdf_btn" onClick={() => navigate('/portfolios')} style={{ border: 'none' }}>
            Back
          </button>
        </div>
        <div>
          <button className="pdf_btn">
            Save PDF
          </button>
        </div>
      </div>

      <div className="portfoiliolist container mt-4">
        <div className="row">
          {posts.map((post) => (
            <div key={post.id} className="col-12 mb-4" onClick={() => handleCardClick(post.id)}>
              <div className="card bg-dark text-white">
                <img src={post.image} className="card-img" alt={post.title} />
                <div className="card-img-overlay d-flex justify-content-between align-items-center">
                  <h5 className="card-title">{post.title}</h5>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Portfolioslist;
