import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import QrScanner from 'react-qr-scanner';
import './Portfolios.css';

const Portfolios = () => {
  const navigate = useNavigate();
  const [isScanning, setIsScanning] = useState(false);

  const handlePortfolioClick = () => {
    navigate('/Portfolioslist');
  };

  const handleScan = (data) => {
    if (data) {
      console.log("QR Code Scanned: ", data);
      setIsScanning(false);
      // Handle the scanned data as needed
    }
  };

  const handleError = (err) => {
    console.error(err);
    setIsScanning(false);
  };

  const handleCameraClick = () => {
    setIsScanning(true);
  };

  return (
    <div className="main_menu_wrapper container">
      <div className="text-center mb-4">
        <h5 className="black-text">ARJUN'S PORTFOLIOS</h5>
      </div>
      <div className="portfoilios">
        <div className="text-center mt-4">
          <div className="qr-code-box p-3 mb-4">
            <div onClick={handleCameraClick}>
              <img src="/scanner.png" alt="Scan QR Code" className="img-fluid" />
            </div>
            <div className="text-center">
              <h3>Camera button</h3>
            </div>
          </div>
        </div>
        {isScanning && (
          <QrScanner
            delay={300}
            onError={handleError}
            onScan={handleScan}
            style={{ width: '100%' }}
          />
        )}
        <div className="row">
          {/* Your portfolio items */}
        </div>
      </div>
    </div>
  );
};

export default Portfolios;
