import React, { useEffect, useState } from 'react';
import './Profile.css';
import 'font-awesome/css/font-awesome.min.css';
import { fetchUser, updateUserDetails } from './services/apiService'; // Import updateUserDetails function

const Profile = () => {
    const [user, setUser] = useState({
        first_name: '',
        last_name: '',
        email: '',
        phone_number: '',
        city: ''
    });
    const [showModal, setShowModal] = useState(false); // State to control modal visibility

    useEffect(() => {
        fetchUser().then(response => {
            const data = response.data || {};
            const nameParts = (data.name || '').split(' ', 2);
            const first_name = nameParts[0] || '';
            const last_name = nameParts[1] || '';

            setUser({
                first_name,
                last_name,
                email: data.email || '',
                phone_number: data.phone_number || '',
                city: data.city || ''
            });
        }).catch(error => {
            console.error("Error fetching user data:", error);
        });
    }, []);

    const handleSaveChanges = () => {
        updateUserDetails(user)
            .then(response => {
                // Show success message modal
                setShowModal(true);
            })
            .catch(error => {
                console.error('Error updating user details:', error);
                alert('Error updating user details');
            });
    };

    const handleCloseModal = () => {
        setShowModal(false);
    };

    return (
        <div className="main_menu_wrapper">
                  <div className='d-flex justify-content-between'>
        <div className='backbtn'>
          <button onClick={handleBackClick}>
          <i className="fa fa-arrow-left"></i>
          </button>
        </div>
        <div>
          <h5 className="black-text">EDIT PROFILE</h5>
        </div>
      </div>
            <h4 className="text-center mb-4 text-dark">EDIT PROFILE</h4>
            <div className="profile-form bg-white p-4 mt-4 rounded">
                <form className="form-content">
                    <div className="form-group mt-4">
                        <div className="input-container">
                            <i className="fa fa-user icon"></i>
                            <input
                                type="text"
                                className="input-field"
                                placeholder="First Name"
                                value={user.first_name}
                                onChange={(e) => setUser({ ...user, first_name: e.target.value })}
                            />
                        </div>
                    </div>

                    <div className="form-group mt-4">
                        <div className="input-container">
                            <i className="fa fa-user icon"></i>
                            <input
                                type="text"
                                className="input-field"
                                placeholder="Last Name"
                                value={user.last_name}
                                onChange={(e) => setUser({ ...user, last_name: e.target.value })}
                            />
                        </div>
                    </div>

                    <div className="form-group mt-4">
                        <div className="input-container">
                            <i className="fa fa-envelope icon"></i>
                            <input
                                type="email"
                                className="input-field"
                                placeholder="Email"
                                value={user.email}
                                onChange={(e) => setUser({ ...user, email: e.target.value })}
                            />
                        </div>
                    </div>

                    <div className="form-group mt-4">
                        <div className="input-container">
                            <i className="fa fa-phone icon"></i>
                            <input
                                type="tel"
                                className="input-field"
                                placeholder="+1234567890"
                                value={user.phone_number}
                                onChange={(e) => setUser({ ...user, phone_number: e.target.value })}
                            />
                        </div>
                    </div>

                    <div className="form-group mt-4">
                        <div className="input-container">
                            <i className="fa fa-map-marker icon"></i>
                            <input
                                type="text"
                                className="input-field"
                                placeholder="City"
                                value={user.city}
                                onChange={(e) => setUser({ ...user, city: e.target.value })}
                            />
                        </div>
                    </div>

                    <button
                        type="button"
                        className="savebtn btn-danger btn-block p-3 mt-4"
                        onClick={handleSaveChanges}
                    >
                        Save Changes
                    </button>
                </form>
            </div>

            {/* Bootstrap Modal */}
            <div className={`modal fade ${showModal ? 'show' : ''}`} style={{ display: showModal ? 'block' : 'none' }} tabindex="-1" role="dialog" aria-labelledby="successModalLabel" aria-hidden={!showModal}>
                <div className="modal-dialog" role="document">
                    <div className="modal-content">
                        <div className="modal-header">
                            <h5 className="modal-title" id="successModalLabel">Success</h5>
                        </div>
                        <div className="modal-body">
                            User details updated successfully!
                        </div>
                        <div className="modal-footer">
                            <button type="button" className="btn btn-secondary" onClick={handleCloseModal}>Close</button>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Profile;
