import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { fetchUser } from './services/apiService'; // Adjust the path as needed
import './Portfolios.css';

const Portfolios = () => {
  const [userName, setUserName] = useState('');
  

  useEffect(() => {
    const getUserData = async () => {
      try {
        const response = await fetchUser(1); // Replace 1 with the user ID you want to fetch
        setUserName(response.data.name);
      } catch (error) {
        console.error('Error fetching user data:', error);
      }
    };

    getUserData();
  }, []);



  const handleCameraInputChange = (event) => {
    const file = event.target.files[0];
    if (file) {
      console.log("File selected: ", file);
      // You can add further processing of the file (e.g., upload it, or use a QR code scanner library)
    }
  };

  return (
    <div className="main_menu_wrapper container">
      <div className="text-center mb-4">
        <h5 className="black-text">{userName ? `${userName}'S PORTFOLIOS` : 'Loading...'}</h5>
      </div>
      <div className="portfolios">
        <div className="text-center mt-4">
          <div className="qr-code-box p-3 mb-4">
            <div>
              <label htmlFor="cameraInput">
                <img src="/scanner.png" alt="Scan QR Code" className="img-fluid" />
              </label>
              <input
                type="file"
                id="cameraInput"
                accept="image/*"
                capture="environment"
                onChange={handleCameraInputChange}
                style={{ display: 'none' }}
              />
            </div>
            <div className="text-center">
              <h3>Scan QR Code</h3>
            </div>
          </div>
        </div>
        <div className="row">
          {/* Portfolio items here */}
        </div>
      </div>
    </div>
  );
};

export default Portfolios;
