import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { Modal, Button, Form } from 'react-bootstrap'; // Import Bootstrap Modal components
import { fetchUserPortfolios, addItemToPortfolio } from './services/apiService'; // Add API methods
import './ProductDetail.css';

const ProductDetail = () => {
  const navigate = useNavigate();
  const [showModal, setShowModal] = useState(false); // State to control modal visibility
  const [portfolios, setPortfolios] = useState([]); // State to store portfolios
  const [selectedPortfolio, setSelectedPortfolio] = useState(''); // State to store selected portfolio
  const [error, setError] = useState(''); // State to store error messages

  // Fetch portfolios on component mount
  useEffect(() => {
    const getPortfolios = async () => {
      try {
        const response = await fetchUserPortfolios(); // Fetch portfolios for the logged-in user
        setPortfolios(response.data);
      } catch (error) {
        console.error('Error fetching portfolios:', error);
      }
    };

    getPortfolios();
  }, []);

  const handleBackClick = () => {
    navigate('/portfolioslist');
  };

  const handleViewInARClick = () => {
    window.location.href = 'https://mywebar.com/p/Project_0_qbtprz8dtu';
  };

  const handleSaveToPortfolioClick = () => {
    setShowModal(true); // Open the modal when "Save to Portfolio" is clicked
  };

  const handleModalClose = () => {
    setShowModal(false); // Close the modal
  };

  const handleFormSubmit = async (e) => {
    e.preventDefault();
    if (!selectedPortfolio) {
      setError('Please select a portfolio.');
      return;
    }

    try {
      await addItemToPortfolio(selectedPortfolio); // Call API to add item to portfolio
      setShowModal(false);
    } catch (error) {
      console.error('Error saving to portfolio:', error);
      setError('Something went wrong, please try again.');
    }
  };

  return (
    <div className='main_menu_wrapper'>
      <div className="container">
        <div className="mb-4">
          <div className='backbtn'>
            <div>
              <button onClick={handleBackClick} style={{ border: 'none' }}>
                Back
              </button>
            </div>
            <div>
              <h5 className="black-text">ARJUN'S PORTFOLIOS</h5>
            </div>
          </div>
        </div>

        <div className='product-container'>
          <div className='image-container'>
            <h5 className="black-text">Creativa Special Effects – Bluish Stone Effect</h5>
            <div className='col-12'>
              <img src="/img2.png" alt="Bluish Stone Effect" className="img-fluid product_img" />
              <p className="product_code">Product Code: <b>BLUISH STONE</b></p>
            </div>
          </div>

          <div className="row justify-content-center mt-4">
            <div className="col-10 col-md-6">
              <p>
                Creativa finishes are known for their urban soul, contemporary style, with material-like effects.
              </p>
              <h5>Product Features:</h5>
              <ul>
                <li>Bluish Stone effect</li>
                <li>Different Variants Available</li>
                <li>Easy to apply</li>
                <li>High Coverage</li>
                <li>Superior Aesthetic</li>
                <li>High Product performance</li>
              </ul>
            </div>
          </div>

          <div className='button-container'>
            <button className="portfolio_btn" onClick={handleSaveToPortfolioClick}>
              <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 576 512" style={{ marginRight: '10px' }}>
                <path fill="#E53935" d="M384 480l48 0c11.4 0 21.9-6 27.6-15.9l112-192c5.8-9.9 5.8-22.1 .1-32.1S555.5 224 544 224l-400 0c-11.4 0-21.9 6-27.6 15.9L48 357.1 48 96c0-8.8 7.2-16 16-16l117.5 0c4.2 0 8.3 1.7 11.3 4.7l26.5 26.5c21 21 49.5 32.8 79.2 32.8L416 144c8.8 0 16 7.2 16 16l0 32 48 0 0-32c0-35.3-28.7-64-64-64L298.5 96c-17 0-33.3-6.7-45.3-18.7L226.7 50.7c-12-12-28.3-18.7-45.3-18.7L64 32C28.7 32 0 60.7 0 96L0 416c0 35.3 28.7 64 64 64l23.7 0L384 480z" />
              </svg> Save to Portfolio
            </button>

            <button className="portfolio_btn" onClick={handleViewInARClick}>
              View in AR
            </button>
          </div>
        </div>

        {/* Modal for selecting portfolio */}
        <Modal show={showModal} onHide={handleModalClose}>
          <Modal.Header closeButton>
            <Modal.Title>Select Portfolio</Modal.Title>
          </Modal.Header>
          <Modal.Body>
            {error && <p className="text-danger">{error}</p>}
            <Form onSubmit={handleFormSubmit}>
              <Form.Group controlId="formPortfolio">
                <Form.Label>Select Wishlist (Portfolio)</Form.Label>
                <Form.Control
                  as="select"
                  value={selectedPortfolio}
                  onChange={(e) => setSelectedPortfolio(e.target.value)}
                  required
                >
                  <option value="">Select a portfolio</option>
                  {portfolios.map((portfolio) => (
                    <option key={portfolio.id} value={portfolio.id}>
                      {portfolio.category}
                    </option>
                  ))}
                </Form.Control>
              </Form.Group>

              <Button variant="primary" type="submit" className="mt-4">
                Save to Portfolio
              </Button>
            </Form>
          </Modal.Body>
        </Modal>
      </div>
    </div>
  );
};

export default ProductDetail;
