import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { logoutUser } from './services/apiService'; // Import the logout function
import './Head.css';

const Head = () => {
    const [isMenuOpen, setIsMenuOpen] = useState(false);
    const navigate = useNavigate();

    const handllogoClick = () => {
        console.log("Logo clicked");
        navigate('/portfolios');
    };

    const handlprofileClick = () => {
        console.log("Profile clicked");
        navigate('/profile');
    };

    const toggleMenu = () => {
        setIsMenuOpen(!isMenuOpen);
    };

    const handleMenuItemClick = (path) => {
        if (path === '/logout') {
            handleLogout();
        } else {
            navigate(path);
            setIsMenuOpen(false); // Close the menu after navigation
        }
    };

    const handleLogout = () => {
        logoutUser()
            .then(() => {
                // Clear local storage or any other cleanup
                localStorage.removeItem('authToken');
                // Redirect to login or home page after logout
                navigate('/login'); // Adjust the path as necessary
            })
            .catch(error => {
                console.error('Logout error:', error);
            });
    };

    return (
        <header>
            <nav className="curve">
                <i 
                    className="bi bi-list" 
                    style={{ fontSize: "2rem", cursor: "pointer" }} 
                    onClick={toggleMenu}
                ></i>
                <img 
                    src="/logo.png" 
                    alt="ICA Logo" 
                    className="img-fluid mainlogo" 
                    onClick={handllogoClick}
                />
                <img 
                    src="/profile.png" 
                    alt="Profile Logo" 
                    className="img-fluid profilelogo" 
                    onClick={handlprofileClick}
                />
            </nav>
            
            {/* Side Menu */}
            {isMenuOpen && (
                <div className="side-menu">
                    <div className="side-menu-content">
                        <i 
                            className="bi bi-x" 
                            style={{ fontSize: "2rem", cursor: "pointer" }} 
                            onClick={toggleMenu}
                        ></i>
                        <div className="menu-header">
                            <img 
                                src="/profile.png" 
                                alt="Profile" 
                                className="profile-picture"
                            />
                            <h3>Arjun Patel</h3>
                        </div>
                        <ul className="menu-items">
                            <li className='btn' onClick={() => handleMenuItemClick('/profile')}><i className="bi bi-person"></i> Profile</li>
                            <li className='btn' onClick={() => handleMenuItemClick('/portfolios')}>Home</li>
                            <li className='btn' onClick={() => handleMenuItemClick('/scan-qr')}>Scan QR Code</li>
                            <li className='btn' onClick={() => handleMenuItemClick('/createportfolio')}>Create New Portfolio</li>
                            <li className='btn' onClick={() => handleMenuItemClick('/logout')}>Sign Out</li>
                        </ul>
                        <div className="menu-footer">
                            <img 
                                src="/logo.png" 
                                alt="ICA Logo" 
                                className="img-fluid"
                            />
                            <p>ITALIAN WOOD FINISHES</p>
                        </div>
                    </div>
                </div>
            )}
        </header>
    );
};

export default Head;
