import React, { useEffect, useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import { fetchPosts, fetchUserWishlistById } from './services/apiService'; // Import your API services
import './Portfolios.css';

const Portfolioslist = () => {
  const [posts, setPosts] = useState([]);
  const [wishlistItems, setWishlistItems] = useState([]);
  const { portfolioId } = useParams(); // Get the portfolio ID from the route params
  const navigate = useNavigate();

  useEffect(() => {
    // Fetch the wishlist items for the selected portfolio
    fetchUserWishlistById(portfolioId)
      .then(response => {
        const wishlist = response.data.items; // Assuming 'items' contains the list of post IDs
        setWishlistItems(wishlist);
      })
      .catch(error => console.error('Error fetching wishlist:', error));
  }, [portfolioId]);

  useEffect(() => {
    // Fetch all posts and filter based on wishlist items
    fetchPosts()
      .then(response => {
        const allPosts = response.data;
        // Filter posts to only include those that match the wishlist items
        const filteredPosts = allPosts.filter(post => wishlistItems.includes(post.id));
        setPosts(filteredPosts);
      })
      .catch(error => console.error('Error fetching posts:', error));
  }, [wishlistItems]);

  const handleBackClick = () => {
    navigate('/portfolios');
  };

  const handleCardClick = (postId) => {
    navigate(`/productdetail/${postId}`);
  };

  const handleSavePdfClick = () => {
    const pdfUrl = '/Product Catalog - Bluish Stone Effect.pdf';
    window.open(pdfUrl, '_blank');
  };

  return (
    <div className="main_menu_wrapper container-fluid">
      <div className='d-flex justify-content-between'>
        <div className='backbtn'>
          <button className="pdf_btn" onClick={handleBackClick} style={{ border: 'none' }}>
            Back
          </button>
        </div>
        <div>
          <button className="pdf_btn" onClick={handleSavePdfClick}>
            <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24">
              <path d="M19 3H5c-1.1 0-2 .9-2 2v14c0 1.1.9 2 2 2h14c1.1 0 2-.9 2-2V5c0-1.1-.9-2-2-2zm0 16H5V5h14v14zm-8-4h2v-2h-2v2zm0-4h2V7h-2v2zm-4 6h10v-1.5H7V15z" />
            </svg> Save PDF
          </button>
        </div>
      </div>

      <div className="portfoiliolist container mt-4">
        <div className="row">
          {posts.map(post => (
            <div className="col-12 mb-4" key={post.id} onClick={() => handleCardClick(post.id)}>
              <div className="card bg-dark text-white">
                <img 
                  src={`http://127.0.0.1:8000/storage/${post.image}`} 
                  className="card-img" 
                  alt={post.title} 
                />
                <div className="card-img-overlay d-flex justify-content-between align-items-center">
                  <h5 className="card-title">{post.title}</h5>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Portfolioslist;
