import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { Modal, Button, Form } from 'react-bootstrap';
import { fetchUserPortfolios, updatePortfolio, fetchPosts } from './services/apiService'; // Import new API methods
import './Portfolios.css';

const Newportfolio = () => {
  const navigate = useNavigate();
  const [showModal, setShowModal] = useState(false);
  const [category, setCategory] = useState('');
  const [selectedItem, setSelectedItem] = useState('');
  const [portfolios, setPortfolios] = useState([]);
  const [posts, setPosts] = useState([]); // State for posts
  const [error, setError] = useState('');

  useEffect(() => {
    const getPortfolios = async () => {
      try {
        const response = await fetchUserPortfolios();
        setPortfolios(response.data);
      } catch (error) {
        console.error('Error fetching portfolios:', error);
      }
    };

    const getPosts = async () => {
      try {
        const response = await fetchPosts();
        setPosts(response.data);
      } catch (error) {
        console.error('Error fetching posts:', error);
      }
    };

    getPortfolios();
    getPosts();
  }, []);

  const handlePortfolioClick = (portfolioId) => {
    navigate(`/Portfolioslist/${portfolioId}`);
  };

  const handleModalOpen = () => setShowModal(true);
  const handleModalClose = () => setShowModal(false);

  const handleFormSubmit = async (e) => {
    e.preventDefault();

    try {
      // Convert selected item to an array
      const itemsArray = selectedItem ? [selectedItem] : [];
      const response = await updatePortfolio(category, itemsArray);
      console.log('Portfolio updated:', response.data);
      setShowModal(false);
      // Optionally refresh portfolios or redirect
      const refreshedResponse = await fetchUserPortfolios();
      setPortfolios(refreshedResponse.data);
    } catch (error) {
      console.error('Error:', error);
      setError('Something went wrong, please try again.');
    }
  };

  return (
    <div className="main_menu_wrapper container">
      <div className="text-center mb-4">
        <h5 className="black-text">SELECT PORTFOLIO TO ADD TO</h5>
      </div>
      <div className="portfolios pt-5">
        <div className="row">
          {portfolios.map((portfolio) => (
            <div key={portfolio.id} className="col-6 col-md-3 mb-4">
              <div className="shadow portfolio-box p-3" onClick={() => handlePortfolioClick(portfolio.id)}>
                <div>
                  <img src="/home.svg" alt={portfolio.category} className="img-fluid mb-2" />
                </div>
                <div className="mt-2">
                  <h4>{portfolio.category}</h4>
                </div>
                <div className="mt-2">
                  <small>{portfolio.items.length} items</small>
                </div>
              </div>
            </div>
          ))}
        </div>

        <div>
          <button className="create_btn" onClick={handleModalOpen}>
            <svg fill="#6c757d" viewBox="0 0 302.816 302.816">
              <path d="M298.423,152.996c-5.857-5.858-15.354-5.858-21.213,0l-35.137,35.136
                c-5.871-59.78-50.15-111.403-112.001-123.706c-45.526-9.055-92.479,5.005-125.596,37.612c-5.903,5.813-5.977,15.31-0.165,21.213
                c5.813,5.903,15.31,5.977,21.212,0.164c26.029-25.628,62.923-36.679,98.695-29.565c48.865,9.72,83.772,50.677,88.07,97.978
                l-38.835-38.835c-5.857-5.857-15.355-5.858-21.213,0.001c-5.858,5.858-5.858,15.355,0,21.213l62.485,62.485
                c2.929,2.929,6.768,4.393,10.606,4.393s7.678-1.464,10.607-4.393l62.483-62.482C304.281,168.352,304.281,158.854,298.423,152.996z"
              />
            </svg>
            <span>Create new</span>
          </button>
        </div>
      </div>

      <Modal show={showModal} onHide={handleModalClose}>
        <Modal.Header closeButton>
          <Modal.Title>Create New Portfolio</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {error && <p className="text-danger">{error}</p>}
          <Form onSubmit={handleFormSubmit}>
            <Form.Group controlId="formCategory">
              <Form.Label>Wishlist Name</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter Wishlist Name"
                value={category}
                onChange={(e) => setCategory(e.target.value)}
                required
              />
            </Form.Group>

            <Form.Group controlId="formItems" className="mt-3">
              <Form.Label>Items</Form.Label>
              <Form.Control
                as="select"
                value={selectedItem}
                onChange={(e) => setSelectedItem(e.target.value)}
                required
              >
                <option value="">Select an item</option>
                {posts.map(post => (
                  <option key={post.id} value={post.id}>
                    {post.title}
                  </option>
                ))}
              </Form.Control>
            </Form.Group>

            <Button variant="primary" type="submit" className="mt-4">
              Submit
            </Button>
          </Form>
        </Modal.Body>
      </Modal>
    </div>
  );
};

export default Newportfolio;
