import React, { useEffect, useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { fetchPosts } from './services/apiService';
import { jsPDF } from 'jspdf'; // Import jsPDF
import './Portfolios.css';

const Portfolioslist = () => {
  const [posts, setPosts] = useState([]);
  const [error, setError] = useState('');
  const navigate = useNavigate();
  const location = useLocation();

  // Extract portfolio data from location state
  const { portfolioId, wishlistItems } = location.state || {};

  useEffect(() => {
    if (!wishlistItems || wishlistItems.length === 0) {
      setError('Your Portfolio is empty');
      return;
    }

    fetchPosts()
      .then(response => {
        const allPosts = response.data;
        const filteredPosts = allPosts.filter(post => wishlistItems.includes(post.id.toString()));
        setPosts(filteredPosts);
      })
      .catch(error => console.error('Error fetching posts:', error));
  }, [wishlistItems]);

  const handleBackClick = () => {
    navigate('/portfolios');
  };

  const handleCardClick = (postId) => {
    navigate(`/portfolios/${postId}`, { state: { portfolioId, wishlistItems } });
  };

  const handleSavePdfClick = () => {
    const doc = new jsPDF();

    // Set PDF title
    doc.setFontSize(20);
    doc.text('Product Catalog', 20, 20);

    let y = 30; // Starting y position for text

    posts.forEach((post, index) => {
      doc.setFontSize(12);
      doc.text(`${index + 1}. ${post.title}`, 20, y);
      y += 10; // Increment y position for next item

      // If you want to add images, you can include the image URLs.
      if (post.image) {
        doc.addImage(`http://127.0.0.1:8000/storage/${post.image}`, 'JPEG', 20, y, 40, 40);
        y += 50; // Increment y position to account for the image size
      }
    });

    // Save the PDF
    doc.save('Product_Catalog.pdf');
  };

  return (
    <div className="main_menu_wrapper container-fluid">
      <div className='d-flex justify-content-between'>
        <div className='backbtn'>
          <button onClick={handleBackClick}>
            <i className="fa fa-arrow-left"></i>
          </button>
        </div>
        <div>
          <button className="pdf_btn" onClick={handleSavePdfClick}>
            <i className="fa fa-download"></i>  Save PDF
          </button>
        </div>
      </div>

      <div className="portfoiliolist container mt-4">
        {error && <div className="alert alert-warning">{error}</div>}
        <div className="row">
          {posts.map(post => (
            <div className="col-12 mb-4" key={post.id} onClick={() => handleCardClick(post.id)}>
              <div className="card bg-dark text-white">
                <img 
                  src={`http://127.0.0.1:8000/storage/${post.image}`} 
                  className="card-img" 
                  alt={post.title} 
                />
                <div className="card-img-overlay d-flex justify-content-between align-items-center">
                  <h5 className="card-title">{post.title}</h5>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Portfolioslist;
